import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';

import { useFormFields } from "../../../libs/hooks/useFormFields.js";

import axios from 'axios';

import React, { useState } from 'react';

import './css/ApplicantSignup.css';

function ApplicantSignup() {
  const [fields, handleFieldChange] = useFormFields({
    firstName: "",
    lastName: "",
    email: "",
    confirmEmail: "",
    password: "",
    confirmPassword: "",
  });
  const [fieldValidationMessage, setFieldValidationMessage] = useState("");

  const onResumeUpload = () => {
    // TODO: figure out how to upload resume
  };

  const validateFields = () => {
    if (fields.firstName.length === 0) {
      setFieldValidationMessage("Please enter a first name");
    } else if (fields.lastName.length === 0) {
      setFieldValidationMessage("Please enter a last name");
    } if (fields.email.length === 0) {
      setFieldValidationMessage("Please enter a email");
    } if (fields.confirmEmail.length === 0) {
      setFieldValidationMessage("Please confirm your email");
    } if (fields.password.length < 6) {
      setFieldValidationMessage("Please enter a password of 6 characters or longer");
    } if (fields.password !== fields.confirmPassword) {
      setFieldValidationMessage("Please ensure your passwords match");
    } if (fields.email !== fields.confirmEmail) {
      setFieldValidationMessage("Please ensure the emails entered match");
    } else if (fields.password !== fields.confirmEmail) {
      setFieldValidationMessage("Please ensure the emails entered match");
    }
  }

  return (
    <div className="root">
      <h1>
        Applicant Signup
      </h1>
      <Form className="form">
        <Form.Group controlId="firstName">
        <Form.Label>First name</Form.Label>
        <Form.Control
            autoFocus
            placeholder="Enter first name"
            value={fields.firstName}
            onChange={handleFieldChange}
        />
        </Form.Group>
        <Form.Group controlId="lastName">
        <Form.Label>Last name</Form.Label>
        <Form.Control
            placeholder="Enter last name"
            value={fields.lastName}
            onChange={handleFieldChange}
        />
        </Form.Group>
        <Form.Group controlId="email">
        <Form.Label>Email address</Form.Label>
        <Form.Control
            type="email"
            placeholder="Enter email"
            value={fields.email}
            onChange={handleFieldChange}
        />
        </Form.Group>
        <Form.Group controlId="confirmEmail">
        <Form.Label>Confirm email address</Form.Label>
        <Form.Control
            type="email"
            placeholder="Confirm email"
            value={fields.confirmEmail}
            onChange={handleFieldChange}
        />
        </Form.Group>
        <Form.Group controlId="password">
        <Form.Label>Password</Form.Label>
        <Form.Control
            type="password"
            placeholder="Enter password"
            value={fields.password}
            onChange={handleFieldChange}
        />
        </Form.Group>
        <Form.Group controlId="confrimPassword">
        <Form.Label>Confirm Password</Form.Label>
        <Form.Control
            type="password"
            placeholder="Confirm Password"
            value={fields.confrimPassword}
            onChange={handleFieldChange}
        />
        </Form.Group>
        <Button className="button" onClick={onResumeUpload} variant="secondary">
          Upload Resume
        </Button>
        <Button className="button" onClick={validateFields} variant="primary">
          Sign Up
        </Button>
        {
            fieldValidationMessage.length !== 0 &&
            <div class="alert alert-danger" role="alert">
              This is a danger alertâ€”check it out!
            </div>
        }
      </Form>
    </div>
  );
}

export default ApplicantSignup;
