import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import React, { useState } from 'react';

function ApplicantSignup() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmedPassword, setConfirmedPassword] = useState("");
  const [isValidPassword, setIsValidPassword] = useState(false);
  const [passwordsMatch, setPasswordsMatch] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  let firstName = React.createRef();

  const onEmailChange = (input) => {
      console.log(this.inputFirstName);
      isSubmitted && setIsSubmitted(false);
      setEmail(input.target.value);
  };

  const onPasswordChange = (input) => {
      isSubmitted && setIsSubmitted(false);
      setPassword(input.target.value);
  };

  const onConfirmPasswordChange = (input) => {
      isSubmitted && setIsSubmitted(false);
      setConfirmedPassword(input.target.value);
  };

  return (
    <div className="root">
      <h1>
        Applicant Signup
      </h1>
      <div className="form">
        <Form>
          <Form.Group>
            <Form.Label>First name</Form.Label>
            <Form.Control
                onChange={(input) => onEmailChange(input)}
                type="email"
                placeholder="Enter email"
                inputRef={(input) => this.inputFirstName = input}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Last name</Form.Label>
            <Form.Control
                onChange={(input) => onEmailChange(input)}
                type="email"
                placeholder="Enter email"
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Email address</Form.Label>
            <Form.Control
                onChange={(input) => onEmailChange(input)}
                type="email"
                placeholder="Enter email"
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Confirm email address</Form.Label>
            <Form.Control
                onChange={(input) => onEmailChange(input)}
                type="email"
                placeholder="Enter email"
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Password</Form.Label>
            <Form.Control
                onChange={(input) => onPasswordChange(input)}
                type="password"
                placeholder="Password"
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
                onChange={(input) => onPasswordChange(input)}
                type="password"
                placeholder="Password"
            />
          </Form.Group>
          <Button className="submitButton" onClick={() => setIsSubmitted(true)} variant="primary">
            Sign Up
          </Button>
        </Form>
      </div>
      {
        isSubmitted &&
        (
          <div>
            <p>Email: {email}</p>
            <p>Password: {password}</p>
          </div>
        )
      }
    </div>
  );
}

export default ApplicantSignup;
